# This file should contain all the record creation needed to seed the database with its default values.
# The data can then be loaded with the rails db:seed command (or created alongside the database with db:setup).
#
# Examples:
#
#   movies = Movie.create([{ name: 'Star Wars' }, { name: 'Lord of the Rings' }])
#   Character.create(name: 'Luke', movie: movies.first)

# Dir.glob(`#{Rails.root}/db/seeds/*.rb`).each { |f| require f}
connection = ActiveRecord::Base.connection()

file_array = ["Columbus_Circle_-_Southwest_Park-polygon.shp", "Great_Hill-polygon.shp", "The_North_Woods-polygon.shp", "North_Meadow-polygon.shp", "East_Meadow-polygon.shp", "The_Great_Lawn-polygon.shp", "The_Reservoir-polygon.shp", "Sheep_Meadow-polygon.shp", "Conservatory_Water-polygon.shp", "The_Pond-polygon.shp", "Harlem_Meer-polygon.shp", "The_Lake_South_Shore-polygon.shp", "The_Mall-polygon.shp", "Cedar_Hill-polygon.shp", "The_Ramble-polygon.shp", "Turtle_Pond-polygon.shp"]

file_array.each do |file|
  shp_sql = `shp2pgsql -c -g geom -W LATIN1 -s 4326 #{Rails.root.join('db', 'shapefiles', file)} locations_ref`
  connection.execute "drop table if exists locations_ref"
  connection.execute shp_sql
  connection.execute <<-SQL
      insert into locations(name, geom, created_at, updated_at)
        select name, geom, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP from locations_ref
  SQL
  connection.execute "drop table locations_ref"
end


