ramble.landmarks.create(name: "The Gill", description: "A stream that travels the Ramble to the lake, it's actually entirely artificial: It can be turned off like a faucet.", image: "http://assets.centralparknyc.org/images/things-to-see-and-do/gill-l.jpg")
