water = Location.find_by(name: "Conservatory Water")
water.update(description: "Inspired by the design of the Grand Basin model boat pond in Paris’ Luxembourg Gardens, Conservatory Water was created by Frederick Law Olmsted and Calvert Vaux as a place where children and adults alike could experience the pleasure of boating.")
water.landmarks.create(name: "Model Boat Pond", description: "Overlooked by Kerbs Boathouse, visitors can bring or rent a model boat to cruise along a pond featured in many childrens' movies. It's also open for ice skating in the winter.", image: "https://live.staticflickr.com/5296/5542816184_600688df0d_b.jpg")
water.landmarks.create(name: "Alice in Wonderland Sculpture", description: "An 11 foot tall bronze sculpture of Alice and her friends, this sculture is actually approved for climbing by the park.", image: "http://www.centralpark.com/downloads/5135/download/alice.jpe?cb=adffc2e95381fc3159eb65b17097d5f8&w=1800&h=")
water.landmarks.create(name: "Hans Christian Andersen Monument", description: "Central Park's Hans Christian Andersen statue was erected in 1956 to commemorate the author's 150th birthday. The large, bronze statue depicts Andersen seated upon a granite bench, reading from his book The Ugly Duckling. Sculpted by Georg John Lober, this children's statue is meant to be climbed on and is a popular attraction for kids.", image: "http://www.centralpark.com/downloads/1748/download/hans-christian-anderson.jpe?cb=783665e2e86dabe11fdbe675f24e26c4&w=800&h=")
