turtle = Location.find_by(name: "Turtle Pond")
turtle.update(description: "A quiet area in the park including a castle and an idyllic swamp, this place gets its name for its large turtle population, released into the pond by owners in the 1980s. This area is also home to the Delacorte Theater, New York's home of Shakespeare.")
turtle.landmarks.create(name: "Belvedere Castle", description: "Functioning as a visitor center, Belvedere Castle looks out on the Great Lawn to the North and the Ramble to the South, and is named for its incredible view. It translates to 'Beautiful View' in Italian.", image: "https://bikerentalcentralpark.com/central-park-bike-guide/wp-content/uploads/2017/06/Belvedere-castle-min.jpg")
turtle.landmarks.create(name: "Delacorte Theater", description: "The home of Shakespeare in New York City, this stage is operated by The Public Theater and produces two plays each summer.", image: "https://imgs.6sqft.com/wp-content/uploads/2018/11/01105908/delacorte-theater-flickr.jpg")
turtle.landmarks.create(name: "Swedish Cottage Marionette Theatre", description: "An actual Swedish Cottage built in 1876 as part of Sweden's exhibit for the Centennial Exposition in Philadelphia, this was brought to Central Park where it remains a permanent theater for marionette and puppet performances", image: "https://live.staticflickr.com/94/247028304_58264b15c0_b.jpg")
turtle.landmarks.create(name: "King Jagiello Monument", description: "A monument to the King of Poland and Grand Duke of Lithuania who lived from 1386-1434, the monument celebrates his victory over the Teutonic ORder in 1410 at the Battle of Grunwald, because someone has to.", image: "http://michaelminn.net/newyork/parks/central-park/monuments/king-jagiello/2007-08-28_15-40-51.jpg")
turtle.facts.create(factoid: "The National Weather Service's measurement of the weather in New York is taken from Belvedere castle.")
turtle.create_reference_to_tag_by_name_user_id_and_review("kids", cheese.id, "Where else can you go for a permanent puppet theater in a Swedish cottage? Nowhere. That's why New York's the best friggin city in the world. God bless.")
turtle.create_reference_to_tag_by_name_user_id_and_review("architecture", cheese.id, "This small area has a castle, a theater, and a Swedish Cottage. It's like a zoo for buildings.")
turtle.create_reference_to_tag_by_name_user_id_and_review("wildlife", cheese.id, "Am I turtly enough for the turtle club?")
turtle.locimages.create(url: "http://www.centralpark.com/downloads/2713/download/turtle-pond.jpe?cb=6438982d0020a2297541b1f816d86c27&w=640", alt: "A picture overlooking turtle pond.")
turtle.locimages.create(url: "https://live.staticflickr.com/7367/26752795904_c6d4d178c3_b.jpg", alt: "The pond from Belvedere Castle.")


glawn = Location.find_by(name: "The Great Lawn")
glawn.update(description: "The heart of Central Park, this is home to countless baseball games, playgrounds, an Obelisk, and more.")
glawn.landmarks.create(name: "The Obelisk", description: "Known as \"Cleopatra's Needle\" by New Yorkers, this is the oldest outdoor monument in New York City, the oldest man-made object in Central Park, and dates to around 1450 BCE, when it was erected along with its partner. Both stood in Alexandria since 18 CE, after which one was moved to London while the other was moved to Central Park, after being offered by the Egyptian Khedive to America in exchange for funds to monetize the country.", image: "http://assets.dnainfo.com/generated/photo/2016/04/obelisk-1459878569.jpg/extralarge.jpg")
glawn.landmarks.create(name: "Alexander Hamilton Monument", description: "Donated to Central Park by Alexander Hamilton's son (he had some that didn't die -- they were cut out of the play), this was placed in 1880.", image: "http://assets.centralparknyc.org/images/things-to-see-and-do/hamilton-l.jpg")
glawn.landmarks.create(name: "Arthur Ross Pinetum", description: "A four acre landscape that is host to 17 different species of pine trees.", image: "http://assets.centralparknyc.org/images/things-to-see-and-do/arthur-ross-pinetum-l.jpg")
glawn.landmarks.create(name: "The Metropolitan Museum of Art", description: "On the East side of Central Park right by the Obelisk is the largest museum in New York and one of the greatest museums in the world.", image: "http://footage.framepool.com/shotimg/qf/705943334-metropolitan-museum-of-art-jacqueline-kennedy-onassis-reservoir-5th-avenue-visions-of-new-york-city.jpg")
glawn.landmarks.create(name: "Summit Rock", description: "West of the Great Lawn lies the highest point of natural elevation in the park. From the West side, you can even see New Jersey, should you want to.", image: "http://www.centralpark.com/downloads/8305/download/summit-rock1.jpg?cb=91f112082dc29141d32bcb6a0438a3fb&w=2048" )
glawn.facts.create(factoid: "The Great Lawn was originally part of New York's reservoir system.")
glawn.create_reference_to_tag_by_name_user_id_and_review("kids", cheese.id, "With more playgrounds than you can shake a stick at and more sticks than you can shake a playground at, your kid will have fun here.")
glawn.create_reference_to_tag_by_name_user_id_and_review("art", cheese.id, "That Obelisk! Wow! And right by the Met.")
glawn.create_reference_to_tag_by_name_user_id_and_review("picnics", cheese.id, "Even on the most crowded day you can always find a spot.")
glawn.locimages.create(url: "http://www.centralpark.com/downloads/5274/download/great-city-great-lawn.jpe?cb=783665e2e86dabe11fdbe675f24e26c4&w=4032", alt: "The lawn with the city in the background.")
glawn.locimages.create(url: "https://d1smv7h0armdzg.cloudfront.net/wp-content/uploads/2014/07/great-lawn-l.jpg", alt: "Crowded day at the Great Lawn as seen overlooking from Turtle Pond.")

reservoir = Location.find_by(name: "The Reservoir")
reservoir.update(description: "Built to hold a billion gallons of water as a temporary water supply while the Croton Water system was shut down for repairs for two weeks, New York now goes through that supply in just four hours. It was decommissioned in 1993, made obsolete by the Third Water Tunnel. It's now a go to spot for joggers.")
reservoir.landmarks.create(name: "South Gatehouse", description: "A small gatehouse overlooking the reservoir.", image: "https://upload.wikimedia.org/wikipedia/commons/e/e2/Central_Park_Reservoir_South_Gatehouse_spring_jeh.jpg")
reservoir.landmarks.create(name: "Gothic Bridge", description: "A gothic style cast iron bridge.", image: "http://assets.centralparknyc.org/images/things-to-see-and-do/bridge-28-l.jpg")
reservoir.landmarks.create(name: "Playgrounds", description: "The reservoir is practically flanked by playgrounds, including the Ancient Playground, Safari Playground, and Tarr-Coyne Wild West Playground", image: "http://assets.centralparknyc.org/images/things-to-see-and-do/ancient-playground-l.jpg")
reservoir.landmarks.create(name: "John Purroy Mitchel Memorial", description: "A monument to the youngest ever mayor in New York City who, after a failed re-election bid, enlisted in WWI to serve in the aviation corps, but fell from his plane during a training flight. This is also where the 23rd mile of the marathon is, at the peak of a fairly long incline. If you want to see New Yorkers at their most miserable, this is the place to go.", image: "http://assets.centralparknyc.org/images/things-to-see-and-do/john-puroy-mitchell-l.jpg")
reservoir.facts.create(factoid: "The full name of the Reservoir is the Jacqueline Kennedy Onassis reservoir, after Jackie Kennedy Onassis, who used to jog along this path.")
reservoir.create_reference_to_tag_by_name_user_id_and_review("exercise", cheese.id, "When I used to jog, I used to aspire to jog here. Of course, I never made it further than my street.")
reservoir.create_reference_to_tag_by_name_user_id_and_review("kids", cheese.id, "It's tragic that they got rid of the rope swing in the Ancient Playground in the renovation. That used to be the best, most dangerous plaground in the city.")
reservoir.locimages.create(url: "http://assets.centralparknyc.org/images/things-to-see-and-do/reservoir.jpg", alt: "Reservoir with Ghostbusters building in background.")
reservoir.locimages.create(url: "http://footage.framepool.com/shotimg/qf/691818177-jacqueline-kennedy-onassis-reservoir-central-park-manhattan-megacity.jpg", alt: "Reservoir as seen from the sky.")

emeadow = Location.find_by(name: "East Meadow")
emeadow.update(description: "The best spot for picnicking in the park, this sprawling high set of hills gives everyone a place to carve out for themselves.")
emeadow.landmarks.create(name: "Robert Bendheim Playground", description: "A small playground if you're uncomfortable with kids just playing on the rocks.", image: "http://www.centralpark.com/downloads/7734/download/DianaRoss1.jpg?cb=9275f2bc77b6559f12ed438bb40884d7&w=640")
emeadow.facts.create(factoid: "Seriously this is the best place for picnicking in the park.")
emeadow.create_reference_to_tag_by_name_user_id_and_review("picnics", cheese.id, "Grill here on Memorial Day. Everyone does.")
emeadow.locimages.create(url: "https://imgs.6sqft.com/wp-content/uploads/2015/12/20035547/Central-Park-East-Meadow-today-1024x679.jpg", alt: "East Meadow from above")
emeadow.locimages.create(url: "http://michaelminn.net/newyork/parks/central-park/east-meadow/2007-08-28_15-58-37.jpg", alt: "East Meadow with one sunbather.")

nmeadow = Location.find_by(name: "North Meadow")
nmeadow.update(description: "Centered around a series of baseball diamonds, this area also is a great place for a stroll.")
nmeadow.landmarks.create(name: "North Meadow Recreation Center", description: "With a preserved outdoor facade, the interior has basketball and handball courts, as well as a community room for classes, gatherings, and recreational activities. They will loan balls, bats, frisbees, and jump ropes.", image: "http://www.centralpark.com/downloads/8591/download/north-meadow-recreation-center.jpg?cb=3d8658d0ca84f9528be9411c0393c953&w=640")
nmeadow.landmarks.create(name: "North Meadow Butterfly Gardens", description: "These four butterfly gardens are run by volunteers to support butterfly populations.", image: "https://s3.amazonaws.com/assets.centralparknyc.org/images/blog/blooms-2.jpg")
nmeadow.landmarks.create(name: "Springbanks Arch", description: "Connecting the North Meadow to the Loch, this stone span also has a cascade that used to be known as Sabrina's Pool.", image: "http://michaelminn.net/newyork/parks/central-park/bridges/springbanks-arch/2007-08-02_18-51-49.jpg")
nmeadow.facts.create(factoid: "The North Meadow Butterfly Gardens provide habitat for Monarch butterflies migrating through New York City on their way to and from Mexico.")
nmeadow.create_reference_to_tag_by_name_user_id_and_review("exercise", cheese.id, "Handball! Now that's a sport you don't see much of in movies anymore.")
nmeadow.create_reference_to_tag_by_name_user_id_and_review("strolling", cheese.id, "You can see so many butterflies!")
nmeadow.locimages.create(url: "http://www.kermitproject.org/newdeal/centralpark/northmeadow6.jpg", alt: "North Meadow baseball field.")
nmeadow.locimages.create(url: "http://assets.centralparknyc.org/images/things-to-see-and-do/North-Meadow-landscape.jpg", alt: "Natural rock in the back of a North Meadow field, shaded by a tree.")

ghill = Location.find_by(name: "Great Hill")
ghill.update(name: "Great Hill and the Pool", description: "The Northwest corner of the park features a hill perfect for picnics and a segment of wetlands that is beautiful in every season.")
ghill.landmarks.create(name: "Peter J. Sharp Children's Glade", description: "Designed specifically as a glade for children to explore nature and play, the segment features stone and tree trunk seating and fun paths.", image: "http://c3314642.r42.cf0.rackcdn.com/00050798-still1.jpg")
ghill.landmarks.create(name: "Glen Span Arch", description: "Glen Span is one of two rustic arches that form the boundaries of the Ravine, the stream valley of the North Woods. Glen Span arches over the stream, called the Loch, at its south end, with Huddlestone Arch at its north end. Both stone structures are sunken into the Park's landscape.", image: "http://assets.centralparknyc.org/images/things-to-see-and-do/glenspan-arch-l.jpg")
ghill.facts.create(factoid: "The pool was originally built by damming a natural stream that was known as Montayne's Rivulet, but now is made of New York City drinking water that comes from a pipe hidden in the rocks on the southern shoreline of the Pool.")
ghill.create_reference_to_tag_by_name_user_id_and_review("strolling", cheese.id, "One of the prettiest areas of the park.")
ghill.create_reference_to_tag_by_name_user_id_and_review("flora", cheese.id, "That weeping willow at the Pool tho")
ghill.create_reference_to_tag_by_name_user_id_and_review("wildlife", cheese.id, "You can see a bunch of different birds, fish, amphibians, anything in the pool!")
ghill.create_reference_to_tag_by_name_user_id_and_review("picnics", cheese.id, "The Great Hill is the spot for picnicking on the West Side.")
ghill.locimages.create(url: "http://assets.centralparknyc.org/images/things-to-see-and-do/great-hill-l.jpg", alt: "Bird's eye view of the pool.")
ghill.locimages.create(url: "http://www.centralpark.com/downloads/4803/download/the-pool.jpe?cb=783665e2e86dabe11fdbe675f24e26c4&w=1200", alt: "The pool, with a weeping willow.")
ghill.locimages.create(url: "http://assets.centralparknyc.org/images/things-to-see-and-do/pool-l.jpg", alt: "rushing water at the pool")

woods = Location.find_by(name: "The North Woods")
woods.update(description: "The closest you can come to a hike in Manhattan and built around the Ravine. While locals will go for a stroll here, it's not unusual to find people in full hiking wear.")
woods.landmarks.create(name: "Loch", description: "This winding, partially natural watercourse runs through the Ravine, and is dammed to create three waterfalls, which are perfect for stumbling upon after a long hike.", image: "http://assets.centralparknyc.org/images/things-to-see-and-do/loch-l.jpg")
woods.landmarks.create(name: "Huddlestone Arch", description: "This arch built from huge stones and with a stream running through it is perhaps the only way to separate the North Woods from the Meer.", image: "http://assets.centralparknyc.org/images/things-to-see-and-do/huddlestone-arch-l.jpg")
woods.landmarks.create(name: "Blockhouse No. 1", description: "This small fort in Central Park, overrun with vines, is the second oldest structure in the park after Cleopatra's Needle.", image: "https://upload.wikimedia.org/wikipedia/commons/1/1b/New_York_City_Blockhouse.JPG")
woods.facts.create(factoid: "The City lets fallen trees lie here, preferring to leave this place to plants and wildlife.")
woods.create_reference_to_tag_by_name_user_id_and_review("hiking", cheese.id, "I saw actual hikers here with a fully packed backpack. It's legit.")
woods.create_reference_to_tag_by_name_user_id_and_review("wildlife", cheese.id, "The North Woods: Not just for raccoons baby.")
woods.locimages.create(url: "https://walksofnewyork.com/blog/wp-content/uploads/2014/05/Ravine-600x381.jpg", alt: "One of the Loch's waterfalls.")
woods.locimages.create(url: "https://tclf.org/sites/default/files/thumbnails/image/7678127744_dc00a81dcd_o.jpg", alt: "Huddlestone Arch as seen from the South.")
woods.locimages.create(url: "https://www.twotravelingtexans.com/wp-content/uploads/2016/03/IMG_3039.jpg", alt: "The woods in fall.")
